mapper.insert:
INSERT INTO site_board
	(
		b_num
		, food_id
		, m_id
		, b_title
		, b_contents
	)
	VALUES
	(
		site_board_seq.NEXTVAL
		, #{food_id}
		, #{m_id}
		, #{b_title}
		, #{b_contents}
	)

mapper.update:
UPDATE site_board
SET	b_readcount = b_readcount + 1
WHERE b_num = #{b_num}

mapper.delete:
DELETE FROM site_board
WHERE
	b_num = #{b_num}
	AND
	m_id = #{m_id}

mapper.update:
UPDATE site_board
SET
	b_title = #{b_title}
	, b_contents = #{b_contents}

	, originalfile = #{originalfile}
	, savedfile = #{savedfile}

WHERE
	b_num = #{b_num}
	AND
	m_id = #{m_id}
