mapper.insert:

		SELECT site_cscenter_seq.currval FROM dual


INSERT INTO site_cscenter
	(
		c_num
		, m_id
		, c_title
		, c_contents
	)
	VALUES
	(
		site_cscenter_seq.NEXTVAL
		, #{m_id}
		, #{c_title}
		, #{c_contents}
	)

mapper.delete:
DELETE FROM site_cscenter
WHERE
	c_num = #{c_num}
	AND
	m_id = #{m_id}

mapper.update:
UPDATE site_cscenter
SET
	c_title = #{c_title}
	, c_contents = #{c_contents}
WHERE
	c_num = #{c_num}
	AND
	m_id = #{m_id}

mapper.insert:
INSERT INTO site_cscenter_reply
