mapper.insert:
	INSERT INTO site_member
		(
		m_id			
	,	 m_pw		
	,	 m_name		
	,	 m_zipcode	
	,	 m_address1
	,	 m_address2  
 	,	 m_phone	
	,	 m_email		 		 
	,	 m_birth 			
 	,	 m_coupon 	
	,	 m_mileage 
		)
		VALUES
		(
	#{m_id}
	, #{m_pw}
	, #{m_name}
	, #{m_zipcode}
	, #{m_address1}
	, #{m_address2}
	, #{m_phone}
	, #{m_email}
	, #{m_birth}
	, #{m_coupon}
	, #{m_mileage}
	)

mapper.update:
UPDATE site_member
SET	m_name=#{m_name}, m_zipcode=#{m_zipcode}, m_phone=#{m_phone}, m_address1=#{m_address1}
, m_address2=#{m_address2}, m_email=#{m_email}, m_birth=#{m_birth}

, m_pw = #{m_pw}

WHERE m_id=#{m_id}
